informalexample.programlisting:
button->on_activate(
   [button=&ns;::make_weak_capture(button)]
   (ONLY IN_THREAD,
    const &ns;::w::callback_trigger_t &trigger,
    const x::w::busy &mcguffin_factory)
   {
       auto got=button.get();
       if (!got)
           return;
       auto & [button]=*got;

       &ns;::ref<&ns;::obj> mcguffin=mcguffin_factory.get_wait_busy_mcguffin();
       button->in_thread_idle(
          [mcguffin]
          (ONLY IN_THREAD)
          {
               // Now create the large window
          });
   });

class flagObj : virtual public &ns;::obj {

public:

    typedef &ns;::mpcobj<bool> value_t;

    value_t value=false;

    void signal()
    {
        value_t::lock lock{value};

        *lock=true;
        lock.notify_all();
    }

    void wait()
    {
        value_t::lock lock{value};

        lock.wait([&] { return *lock; });
    }
};

void flush(const &ns;::w::element &e)
{
    auto flag=&ns;::ref<flagObj>::create();

    e->in_thread_idle([flag]
              (THREAD_CALLBACK)
              {
                  flag->signal();
              });

    flag->wait();
}
